fragment:
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:x_linked_myopathy_with_excessive_autophagy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :progressive_weakness ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :muscle_weakness ],
        :myopathy,
        :x_linked_recessive_disease ;
    :has_definition "A myopathy that is characterized by childhood onset of progressive muscle weakness and atrophy primarily affecting the proximal muscles in males between 5 and 10 years old, has_material_basis_in mutation in the VMA21 gene on chromosome Xq28." ;
    :has_exact_synonym "XMEA"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:adenylosuccinase_lyase_deficiency rdfs:subClassOf :amino_acid_metabolic_disorder ;
    :has_definition "An amino acid metabolic disorder that is characterized by microcephaly, aggressive behavior, cerebellar hypoplasia and seizures, has_material_basis_in autosomal recessive inheritance of mutation in the ADSL gene resulting in adenylosuccinate lyase deficiency." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:arc_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome that is characterized by congenital joint contractures, renal tubular dysfunction, cholestasis, severe failure to thrive, ichthyosis and a defect in platelet alpha-granule biogenesis and that has_material_basis_in homozygous or compound heterozygous mutation in the VPS33B gene or homozygous or compound heterozygous mutation in the VIPAR gene on chromosome 14q24.3." ;
    :has_exact_synonym "ARTHROGRYPOSIS, RENAL DYSFUNCTION, AND CHOLESTASIS"@en,
        "Arthrogryposis, renal dysfunction and cholestasis (ARC) syndrome"@en,
        "Arthrogryposis-renal dysfunction-cholestasis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:armfield_syndrome rdfs:subClassOf :syndromic_x_linked_intellectual_disability ;
    :has_definition "A syndromic X-linked intellectual disability characterized by intellectual disability, short stature, seizures, and small hands and feet and in some cases cleft palate or cataracts/glaucoma that has_material_basis_in variation in the chromosomal region Xq28." ;
    :has_exact_synonym "Armfield X-linked mental retardation syndrome"@en,
        "MRXSA"@en,
        "X-linked intellectual disability, Armfield type"@en,
        "mental retardation syndrome, X-linked, Armfield type"@en,
        "syndromic X-linked mental retardation Armfield type"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:neuroacanthocytosis rdfs:subClassOf :neurodegenerative_disease ;
    :has_definition "A neurodegenerative disease that is characterized by characterized by misshapen, spiny red blood cells (acanthocytosis) and neurological abnormalities, especially movement disorders." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:choreaacanthocytosis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :neuroacanthocytosis ;
    :has_definition "A neuroacanthocytosis characterized by progressive neurodegeneration and red cell acanthocytosis, with onset in the third to fifth decade of life and has_material_basis_in homozygous or compound heterozygous mutation in the VPS13A gene, which encodes chorein, on chromosome 9q21." ;
    :has_exact_synonym "Levine-Critchley syndrome"@en,
        "choreo-acanthocytosis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:midface_dysplasia rdfs:subClassOf :osteochondrodysplasia ;
    :has_definition "An osteochondrodysplasia that is characterized by hypertelorism, a wide nose, vertical midline cleft and encephalocele." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mitochondrial_complex_v_atp_synthase_deficiency_nuclear_type_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :mitochondrial_complex_v_atp_synthase_deficiency ;
    :has_definition "A mitochondrial complex V (ATP synthase) deficiency that has_material_basis_in mutation in the ATPAF2 gene on chromosome 17p11." ;
    :has_exact_synonym "MC5DN1"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:n_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome that is characterized by intellectual disability, deafness, ocular abnormalities, T-cell leukemia, cryptorchidism, hypospadias and spasticity." ;
    :has_exact_synonym "NSX"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:polycystic_liver_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :liver_disease ;
    :has_definition "A liver disease that is characterized by the presence of multiple cysts located_in the liver." ;
    :has_exact_synonym "congenital cystic liver disease"@en,
        "congenital hepatic cyst"@en,
        "fibrocystic liver disease"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:pheochromocytoma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :high_blood_pressure ],
        :endocrine_organ_benign_neoplasm ;
    :has_definition "An endocrine organ benign neoplasm that arises within the adrenal medulla, releasing epinephrines and norepinephrines hormones that cause either episodic or persistent high blood pressure." ;
    :has_exact_synonym "phaeochromocytoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:spastic_ataxia_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :spastic_ataxia ;
    :has_definition "A spastic ataxia characterized by early-onset cerebellar ataxia, spasticity, pyramidal syndrome and peripheral neuropathy, has_material_basis_in autosomal dominant inheritance of heterozygous mutation in the VAMP1 gene on chromosome 12p13." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:paraganglioma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :pheochromocytoma ;
    :has_definition "A pheochromocytoma that arises in extraadrenal sympathetic ganglia." ;
    :has_exact_synonym "chemodectoma"@en,
        "glomus body tumor"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:rapadilino_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :diarrhea ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome that is characterized by radial ray defect, patellae hypoplasia, cleft palate, diarrhea, dislocated joints, limb malformations, long nose and normal intelligence, has_material_basis_in homozygous or compound heterozygous mutation in the DNA helicase gene RECQL4 on chromosome 8q24." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:schneckenbecken_dysplasia rdfs:subClassOf :osteochondrodysplasia ;
    :has_definition "An osteochondrodysplasia that is characterized by hypoplastic iliac bones, hypoplastic vertebral bodies, short ribs and broad long bones, prenatally lethal, has_material_basis_in homozygous or compound heterozygous mutation in the SLC35D1 gene on chromosome 1p31." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:non_syndromic_x_linked_intellectual_disability rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_inheritance ],
        :non_syndromic_intellectual_disability,
        :x_linked_monogenic_disease ;
    :has_definition "A non-syndromic intellectual disability characterized by a X-linked inheritance pattern." ;
    :has_exact_synonym "non-specific X-linked mental retardation"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:joubert_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :brain ],
        :brain_disease,
        :ciliopathy ;
    :has_definition "A ciliopathy that is characterized by congenital malformation of the brainstem and agenesis or hypoplasia of the cerebellar vermis leading to an abnormal respiratory pattern, nystagmus, hypotonia, ataxia, and delay in achieving motor milestones." ;
    :has_exact_synonym "JBTS"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:meckel_syndrome rdfs:subClassOf :ciliopathy ;
    :has_definition "A ciliopathy that is characterized by renal cystic dysplasia, occipital encephalocele, polydactyly (post axial), hepatic developmental defects, and pulmonary hypoplasia." ;
    :has_exact_synonym "Meckel-Gruber syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hydrolethalus_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by mulitple fetal developmental defects including polydactyly and central nervous system malformations that results from a single amino acid mutation of D211G of the HYLS1 gene which plays a central role in cilia formation." ;
    :has_exact_synonym "Salonen-Herva-Norio syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ogden_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_inheritance ],
        :syndrome,
        :x_linked_monogenic_disease ;
    :has_definition "A syndrome characterized by postnatal growth failure, severely delayed psychomotor development, variable dysmorphic features, and hypotonia and has_material_basis_in X-linked recessive or X-linked dominant mutation in the NAA10 gene on chromosome Xq28." ;
    :has_exact_synonym "N-alpha-acetyltransferase"@en,
        "N-terminal acetyltransferase deficiency"@en,
        "OGDNS"@en,
        "X-linked Malformation and Infantile Lethality Syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:zollinger_ellison_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome that is characterized by the development of a one or more tumors (gastrinoma) in the pancreas, duodenum, or both that secrete excessive levels of gastrin, a hormone that stimulates production of acid by the stomach." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:secondary_progressive_multiple_sclerosis rdfs:subClassOf :multiple_sclerosis ;
    :has_definition "A multiple sclerosis that is characterized by a worsening of disability, rather than by relapses followed by recovery, which progressively worsens until a steady progression of disability replaces the cycles of attacks." ;
    :has_exact_synonym "SPMS"@en,
        "Secondary-progressive MS"@en ;
    :has_narrower_match "MESH:D020528" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:primary_progressive_multiple_sclerosis rdfs:subClassOf :multiple_sclerosis ;
    :has_definition "A multiple sclerosis that is characterized by steady worsening of neurologic functioning, without any distinct relapses or periods of remission. The rate of progression may vary over time, with occasional plateaus or temporary improvements, but the progression is continuous." ;
    :has_exact_synonym "PPMS"@en,
        "Primary-progressive MS"@en ;
    :has_narrower_match "MESH:D020528" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:progressive_relapsing_multiple_sclerosis rdfs:subClassOf :multiple_sclerosis ;
    :has_definition "A multiple sclerosis that is characterized by steadily worsening symptoms and attacks during periods of remission with disease progression from the onset." ;
    :has_exact_synonym "PRMS"@en,
        "Progressive-relapsing MS"@en ;
    :has_narrower_match "MESH:D020528" .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:iridogoniodysgenesis_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :iris_disease ;
    :has_definition "An iris disease that is characterized by the iris stroma being hypoplastic resulting from abnormalities in the differentiation of the anterior segment structures and increased values of intraocular pressure and has_material_basis_in autosomal dominant inheritance of mutations in the PITX2 gene." ;
    :has_exact_synonym "IGDS"@en,
        "IRID 1"@en,
        "IRID 2"@en,
        "iridogoniodysgenesis type 1"@en,
        "iridogoniodysgenesis type 2"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:juvenile_polyposis_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :young_adult_onset ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :gastrointestinal_system_disease ;
    :has_definition "A gastrointestinal system disease that is characterized by predisposition to hamartomatous benign polyps in the gastrointestinal tract, specifically in the stomach, small intestine, colon, and rectum." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:proximal_symphalangism rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :bone_element ],
        :autosomal_dominant_disease,
        :bone_disease,
        :syndrome ;
    :has_definition "A syndrome that is characterized by ankylosis of the proximal interphalangeal joints, carpal and tarsal bone fusion, and conductive deafness." ;
    :has_exact_synonym "Cushing's symphalangism"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:tarsal_carpal_coalition_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :dysostosis ;
    :has_definition "A dysostosis that is characterized by fusion of the carpals, tarsals, and phalanges, short first metacarpals causing brachydactyly; and humeroradial fusion." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:fibular_hypoplasia_and_complex_brachydactyly rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :bone_element ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :acromesomelic_dysplasia,
        :autosomal_recessive_disease ;
    :has_definition "An acromesomelic dysplasia that is characterized by severe reduction or absence of the fibula and complex brachydactyly." ;
    :has_exact_synonym "acromesomelic dysplasia-2B",
        "Du Pan syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:persistent_mullerian_duct_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :reproductive_system ],
        :pseudohermaphroditism,
        :reproductive_system_disease ;
    :has_definition "A pseudohermaphroditism that is characterized by the persistence of Mullerian duct derivatives (i.e. uterus, cervix, fallopian tubes and upper two thirds of vagina) in a phenotypically and karyotypically male." ;
    :has_exact_synonym "persistent Muellerian duct syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:multiple_cutaneous_and_mucosal_venous_malformations rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :vein ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :zone_of_skin ],
        :autosomal_dominant_disease,
        :skin_disease,
        :vein_disease ;
    :has_definition "A vein disease that is characterized by multiple bluish cutaneous or mucosal venous lesions that has_material_basis_in heterozygous mutation in the TEK gene on chromosome 9p21." ;
    :has_exact_synonym "cutaneomucosal venous malformation",
        "VMCM"@en,
        "mucocutaneous venous malformations"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:short_qt_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :arrhythmia ],
        :heart_conduction_disease ;
    :has_definition "A heart conduction disease that is characterized by heart arrhythmia defined as a short QT interval on an EKG (less than 300 ms) that does not significantly change with heart rate, tall and peaked T waves, and a structurally normal heart." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:multiple_synostoses_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :dysostosis ;
    :has_definition "A dysostosis that is characterized by characterized by multiple joint fusions, usually commencing in the hands, conductive deafness, and characteristic facial features, including a broad, tubular-shaped nose and a thin upper vermilion." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cone_dystrophy rdfs:subClassOf :retinal_disease ;
    :has_definition "A retinal disease that is characterized by the loss of cone cells, the photoreceptors responsible for both central and color vision." ;
    :has_exact_match "MESH:D000077765" ;
    :has_exact_synonym "retinal cone dystrophy"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:achalasia_microcephaly_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_feature ;
            owl:someValuesFrom :achalasia ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_feature ;
            owl:someValuesFrom :microcephaly ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :vomiting ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_feature ;
            owl:someValuesFrom :intellectual_disability ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome that is characterized by microcephaly, intellectual deficit and early onset symptoms of achalasia (abnormal enlargement of the esophagus, impaired peristalsis, cardiospasm, recurrent vomiting and respiratory infections)." ;
    :has_exact_synonym "ACHALASIA-MICROCEPHALY SYNDROME"@en,
        "Achalasia microcephaly"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:peroxisomal_acyl_coa_oxidase_deficiency rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :peroxisomal_disease ;
    :has_definition "A peroxisomal disease that is characterized by hypotonia and seizures in the neonatal period and neurological regression in early infancy that has_material_basis_in homozygous mutation in the ACOX1 gene on chromosome 17q25.1." ;
    :has_exact_match "GARD:4543",
        "NCI:C170437",
        "ORDO:2971",
        "SNOMEDCT_US_2023_03_01:238069004",
        "UMLS_CUI:C1849678" ;
    :has_exact_synonym "Peroxisomal acyl-coenzyme A oxidase"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cerebral_creatine_deficiency_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :cerebrum ],
        :amino_acid_metabolic_disorder ;
    :has_definition "An amino acid metabolic disorder that is characterized by mental retardation, severe speech delay, behavioral abnormalities and seizures, has_material_basis_in mutation in the creatine transporter gene on chromosome Xq28." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:guanidinoacetate_methyltransferase_deficiency rdfs:subClassOf :cerebral_creatine_deficiency_syndrome ;
    :has_definition "A cerebral creatine deficiency syndrome that is characterized by severe intellectual disability, seizures, speech problems and involuntary movements, has_material_basis_in homozygous or compound heterozygous mutation in the GAMT gene on chromosome 19p13." ;
    :has_exact_synonym "Cerebral creatine deficiency syndrome 2"@en,
        "GAMT deficiency"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cerebral_creatine_deficiency_syndrome_1 rdfs:subClassOf :cerebral_creatine_deficiency_syndrome ;
    :has_definition "A cerebral creatine deficiency syndrome that is characterized by mental retardation, severe speech delay, behavioral abnormalities and seizures, has_material_basis_in mutation in the SLC6A8 gene on chromosome Xq28." ;
    :has_exact_synonym "creatine transporter deficiency",
        "CEREBRAL CREATINE DEFICIENCY SYNDROME 1"@en,
        "SLC6A8 deficiency"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:androgenic_alopecia rdfs:subClassOf :alopecia ;
    :has_definition "An alopecia that is characterized by M-shaped hair line recession and thinning of hair at the crown of the head in males." ;
    :has_exact_synonym "alopecia androgenetica, male pattern baldness"@en,
        "androgenetic alopecia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ehlers_danlos_syndrome_spondylodysplastic_type_2 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :ehlers_danlos_syndrome ;
    :has_definition "An Ehlers-Danlos syndrome that is characterized by aged appearance, developmental delay, short stature, craniofacial disproportion, generalized osteopenia, defective wound healing, hypermobile joints, hypotonic muscles, and loose but elastic skin and has_material_basis_in compound heterozygous mutation in the B3GALT6 gene." ;
    :has_exact_synonym "Ehlers-Danlos syndrome progeroid type",
        "EHLERS-DANLOS SYNDROME, PROGEROID TYPE, 2"@en ;
    :has_related_synonym "XGPT deficiency"@en,
