    :has_exact_synonym "Ochoa syndrome"@en,
        "hydronephrosis with peculiar facial expression"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:stargardt_disease rdfs:subClassOf :age_related_macular_degeneration ;
    :has_definition "An age related macular degeneration that is characterized by progressive vision loss usually to the point of legal blindness." ;
    :has_exact_synonym "STARGARDT DISEASE 1"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

