    :has_definition "A heart conduction disease that is characterized by the impairment of the conduction between the atria and ventricles of the heart." ;
    :has_exact_match "MESH:D054537" ;
    :has_exact_synonym "AV block"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

