:integumentary_system_benign_neoplasm rdfs:subClassOf :integumentary_system_disease,
        :organ_system_benign_neoplasm ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :benign_neoplasm ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :integumental_system ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "An organ system benign neoplasm located_in the integumentary system organs." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:integumentary_system_cancer rdfs:subClassOf :integumentary_system_disease,
        :organ_system_cancer ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :cancer ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :integumental_system ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "An organ system cancer that is located_in the skin, hair and nails." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:connective_tissue_benign_neoplasm rdfs:subClassOf :connective_tissue_disease,
        :musculoskeletal_system_benign_neoplasm ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :benign_neoplasm ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :connective_tissue ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A musculoskeletal system benign neoplasm that is located_in connective tissue." ;
    :has_exact_synonym "mesenchymal tissue neoplasm",
        "neoplasm of soft tissue"@en,
        "neoplasm of soft tissues"@en,
        "soft tissue benign neoplasm"@en,
        "tumor of the soft tissue"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:heavy_chain_disease rdfs:subClassOf :hypersensitivity_reaction_type_iv_disease ;
    :has_definition "A hypersensitivity reaction type IV disease that results from a proliferation of cells producing immunoglobulin heavy chains." ;
    :has_exact_match "MESH:D006362" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:alpha_chain_disease rdfs:subClassOf :heavy_chain_disease ;
    :has_definition "A heavy chain disease that results from an overproduction of alpha antibodies (IgA)." ;
    :has_exact_synonym "Seligmann's disease"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:gamma_heavy_chain_disease rdfs:subClassOf :heavy_chain_disease ;
    :has_definition "A heavy chain disease that results from an overproduction of gamma antibody (IgG)." ;
    :has_exact_synonym "Franklin's disease"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mu_chain_disease rdfs:subClassOf :heavy_chain_disease ;
    :has_definition "A heavy chain disease that results from an overproduction of mu antibody (IgM)." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:delta_chain_disease rdfs:subClassOf :heavy_chain_disease ;
    :has_definition "A heavy chain disease that results from an overproduction of delta antibody (IgD)." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:akinetopsia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of motion perception." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:alexithymia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a deficiency in understanding, processing, or describing emotions." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:amusia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognize musical notes, rhythms, and intervals." ;
    :has_exact_synonym "receptive amusia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:anosognosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to gain feedback about one's own condition or impairments." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:apperceptive_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to distinguish visual shapes." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:apraxia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to map out physical actions in order to repeat them in functional activities." ;
    :has_exact_match "MESH:D001072" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:associative_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognize visual scenes or classes of objects but retain the ability to describe them." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:auditory_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to distinguishing environmental and non-verbal auditory cues including difficulty distinguishing speech from non-speech sounds even though hearing is usually normal." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autotopagnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to orient parts of the body." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:color_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognize a color, while being able to perceive or distinguish it." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cortical_deafness rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to perceive any auditory information but whose hearing is intact." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:finger_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to distinguish the fingers on the hand." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:form_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to perceive a whole object while perceiving only parts of details." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:integrative_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to integrate these elements together into comprehensible perceptual wholes while recognizing elements of the whole." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mirror_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to acknowledge objects in the neglected field that are visible when a mirror reflects the object visible in the non-neglected field." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:pain_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to perceive and process pain." ;
    :has_exact_synonym "analgesia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:phonagnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognize familiar voices." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:semantic_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to visually recognise an object while maintaining the use of non-visual sensory systems such as feeling, tapping, smelling, rocking or flicking the object to recognise the object." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:simultanagnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recgonize a whole image or scene while retianing the ability to recognize objects or details in their visual field one at a time." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:social_emotional_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to perceive facial expression, body language and intonation, rendering them unable to non-verbally perceive people's emotions and limiting that aspect of social interaction." ;
    :has_exact_synonym "expressive agnosia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:astereognosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is the loss of the ability to recognize objects by touch based on its texture, size and weight." ;
    :has_exact_synonym "somatosensory agnosia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:tactile_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognize or identify objects by touch alone." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:time_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to comprehend the succession and duration of events." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:topographical_agnosia rdfs:subClassOf :visual_agnosia ;
    :has_definition "An agnosia that is a loss of the ability to rely on visual cues to guide them directionally due to the inability to recognise objects." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:verbal_auditory_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to recognising spoken words as semantically meaningful." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:visual_agnosia rdfs:subClassOf :agnosia ;
    :has_definition "An agnosia that is a loss of the ability to visually recognize objects." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:visual_verbal_agnosia rdfs:subClassOf :visual_agnosia ;
    :has_definition "An agnosia that is a loss of the ability to comprehending the meaning of written words." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:diffuse_alopecia_areata rdfs:subClassOf :alopecia_areata ;
    :has_definition "An alopecia areata that involves diffuse loss of hair over the whole scalp." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acquired_metabolic_disease rdfs:subClassOf :disease_of_metabolism ;
    :has_definition "A disease of metabolism that has_material_basis_in enzyme deficiency or accumulation of enzymes or toxins which interfere with normal function due to an endocrine organ disease, organ malfunction, inadequate intake, dietary deficiency, or malabsorption." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:organic_acidemia rdfs:subClassOf :amino_acid_metabolic_disorder ;
    :has_definition "An amino acid metabolic disorder that disrupts normal amino acid metabolism causing a building up of branched-chain amino acids." ;
    :has_exact_synonym "organic acid metabolism disorder"@en,
        "organic aciduria"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:childhood_spinal_muscular_atrophy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :childhood_onset ],
        :spinal_muscular_atrophy ;
    :has_definition "A spinal muscular atrophy that is associated with the survival of motor neuron protein in childhood and that has_material_basis_in variation in the SMN gene." ;
    :has_exact_synonym "survival motor neuron spinal muscular atrophy",
        "spinal muscular atrophies of childhood"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:kennedy_s_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        :spinal_muscular_atrophy,
        :x_linked_recessive_disease ;
    :has_definition "A spinal muscular dystrophy that has_material_basis_in an X-linked recessive expansion of CAG triplet repeats (glutamine) in exon 1 of AR gene encoding the androgen receptor." ;
    :has_exact_synonym "Kennedy disease"@en,
        "SBMA"@en,
        "Spinobulbar Muscular Atrophy"@en,
        "X-Linked Bulbo-Spinal Atrophy"@en,
        "X-linked Spinal and Bulbar Muscular Atrophy"@en,
        "spinal bulbar muscular atrophy"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:dentatorubral_pallidoluysian_atrophy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_phenotype ;
            owl:someValuesFrom :spinocerebellar_tract_degeneration ],
        :autosomal_dominant_cerebellar_ataxia ;
    :has_definition "An autosomal dominant cerebellar ataxia that has_material_basis_in expansion of CAG triplet repeats (glutamine) encoding a polyglutamine tract in the atrophin-1 protein." ;
    :has_exact_match "MESH:D020191",
        "NCI:C122653",
        "UMLS_CUI:C0751781" ;
    :has_exact_synonym "DRPLA"@en,
        "Haw River Syndrome"@en,
        "Naito-Oyanagi disease"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:body_dysmorphic_disorder rdfs:subClassOf :somatoform_disorder ;
    :has_definition "A somatoform disorder that involves an excessive concern about and preoccupation with a perceived defect in his or her physical features (body image)." ;
    :has_exact_synonym "body dysmorphia"@en,
        "dysmorphic syndrome"@en,
        "dysmorphophobia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:pain_disorder rdfs:subClassOf :somatoform_disorder ;
    :has_definition "A somatoform disorder that involves chronic pain in one or more areas, and is thought to be caused by psychological stress." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:kleine_levin_syndrome rdfs:subClassOf :recurrent_hypersomnia ;
    :has_definition "A recurrent hypersomnia that is characterized by recurring periods of excessive amounts of sleep and altered behavior." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bipolar_ll_disorder rdfs:subClassOf :bipolar_disorder ;
    :has_definition "A bipolar disorder that is characterized by at least one hypomanic episode and at least one major depressive episode; with this disorder, depressive episodes are more frequent and more intense than manic episodes." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:seasonal_affective_disorder rdfs:subClassOf :depressive_disorder ;
    :has_definition "A mental depression that involves presentation of depressive symptoms only during a specific season of the year." ;
    :has_exact_synonym "winter depression"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:histidinemia rdfs:subClassOf :histidine_metabolism_disease ;
    :has_definition "A histidine metabolism disease characterized by a deficiency of the enzyme histidase." ;
    :has_exact_synonym "histidinuria"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:benign_familial_infantile_epilepsy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :infantile_onset ],
        :infancy_electroclinical_syndrome ;
    :has_definition "An infancy electroclinical syndrome that is characterized by afebrile partial complex or generalized tonic-clonic seizures occurring between 3 and 12 months of age with a good response to medication and no neurologic sequelae." ;
    :has_exact_synonym "BFIC"@en,
        "BFIE"@en,
        "benign familial infantile convulsion"@en,
        "benign familial infantile seizures"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:generalized_epilepsy_with_febrile_seizures_plus rdfs:subClassOf :idiopathic_generalized_epilepsy ;
    :has_definition "An idiopathic generalized epilepsy that is characterized by febrile seizures often with accessory afebrile generalized tonic-clonic seizures with childhood onset." ;
    :has_exact_synonym "GEFS+"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:juvenile_absence_epilepsy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :young_adult_onset ],
        :adolescence_adult_electroclinical_syndrome ;
    :has_definition "An adolescence-adult electroclinical syndrome statring between the age of ten to 17 years characterized by the occurrence of typical absence seizures." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:timothy_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_phenotype ;
            owl:someValuesFrom :neurodevelopmental_abnormality ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :syndrome ;
    :has_broader_match "UMLS_CUI:C1832916" ;
    :has_definition "A syndrome characterized by cardiac, hand/foot, facial, and neurodevelopmental features that has_material_basis_in heterozygous mutation in the CACNA1C gene on chromosome 12p13.33. The two forms are type 1 (classic) and type 2, a rare form that has_material_basis_in mutations in a transcript variant of the CACNA1C gene." ;
    :has_related_synonym "long QT syndrome with syndactyly" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:gaba_aminotransferase_deficiency rdfs:subClassOf :gamma_amino_butyric_acid_metabolism_disorder ;
    :has_definition "A gamma-amino butyric acid metabolism disorder that is characterized by a defect in the gene coding for gamma-aminobutyrate transaminase, which is responsible for catabolism of gamma-aminobutyric acid (GABA), an important, mostly inhibitory neurotransmitter in the central nervous system, into succinic semialdehyde." ;
    :has_exact_synonym "Gamma-amino butyric acid transaminase deficiency"@en,
        "gamma-aminobutyric acid transaminase deficiency"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:succinic_semialdehyde_dehydrogenase_deficiency rdfs:subClassOf :gamma_amino_butyric_acid_metabolism_disorder ;
    :has_definition "A gamma-amino butyric acid metabolism disorder that is characterized by a deficiency of succinic semialdehyde dehydrogenase resulting in elevated levels of gamma-hydroxybutyric acid." ;
    :has_exact_synonym "4-hydroxybutyric aciduria"@en,
        "SSADH"@en,
        "gamma-hydroxybutyric aciduria"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:gamma_amino_butyric_acid_metabolism_disorder rdfs:subClassOf :amino_acid_metabolic_disorder ;
    :has_definition "An amino acid metabolic disorder characterized by impairment of the GABA catabolic pathway." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:homocarnosinosis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :brain ],
        :brain_disease,
        :gamma_amino_butyric_acid_metabolism_disorder ;
    :has_definition "A gamma-amino butyric acid metabolism disorder that is characterized by an excess of homocarnosine in the brain and has_material_basis_in a deficiency of serum carnosinase in its ability to hydrolyze homocarnosine." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:familial_hemiplegic_migraine rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :drowsiness ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :confusion ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :muscle_weakness ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :hemiparesis ],
        :migraine_with_aura ;
    :has_definition "A migraine with aura that is characterized by temporary numbness or weakness, often affecting one side of the body (hemiparesis). Additional features of an aura can include difficulty with speech, confusion, and drowsiness." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:renpenning_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :short_stature ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        :syndromic_x_linked_intellectual_disability,
        :x_linked_recessive_disease ;
    :has_definition "An intellectual disability that is characterized by small head size (microcephaly), long narrow face, short stature, small testes, and intellectual deficit which follows X-linked inheritance and presents most often in males." ;
    :has_exact_synonym "Golabi-Ito-Hall syndrome"@en,
        "Sutherland-Haan X-linked mental retardation syndrome"@en,
        "X-linked intellectual disability due to PQBP1 mutations"@en,
        "X-linked intellectual disability, Renpenning type"@en,
        "X-linked mental retardation Renpenning type"@en,
        "X-linked mental retardation with spastic diplegia"@en,
        "syndromic X-linked mental retardation 8"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:colitis rdfs:subClassOf :inflammatory_bowel_disease ;
    :has_definition "An inflammatory bowel disease that involves inflammation located_in colon." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ischemic_colitis rdfs:subClassOf :colitis ;
    :has_definition "A colitis caused_by inadequate blood supply to the colon." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:microscopic_colitis rdfs:subClassOf :colitis ;
    :has_definition "A colitis that can only be diagnosed_by the examination of colon tissue under a microscope." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:collagenous_colitis rdfs:subClassOf :microscopic_colitis ;
    :has_definition "A colitis characterized by a distinctive thickening of the subepithelial collagen table." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:lymphocytic_colitis rdfs:subClassOf :microscopic_colitis ;
    :has_definition "A colitis characterized by an accumulation of lymphocytes in the colonic epithelium and connective tissue (lamina propria)." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:clostridium_difficile_colitis rdfs:subClassOf :colitis ;
    :has_definition "A colitis characterized by an overgrowth of Clostridium difficile bacteria." ;
    :has_exact_synonym "Pseudomembranous colitis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chemical_colitis rdfs:subClassOf :colitis ;
    :has_definition "A colitis caused_by introduction of various chemicals." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:diversion_colitis rdfs:subClassOf :colitis ;
    :has_definition "A colitis caused_by diversion of the fecal stream due to complication of ileostomy or colostomy." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:jejunoileitis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :cramp ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :diarrhea ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :abdominal_cramp ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :abdominal_pain ],
        :crohn_s_disease ;
    :has_definition "An inflammatory bowel disease that is characterized by patchy areas of inflammation located_in jejunum, has_symptom abdominal pain, has_symptom diarrhea, has_symptom cramps and has_symptom formation of fistulas." .

