fragment:
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mood_disorder rdfs:subClassOf :cognitive_disorder ;
    :has_definition "A cognitive disorder that involves a disturbance in mood as the predominant underlying feature." ;
    :has_exact_synonym "episodic mood disorder"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:purpura rdfs:subClassOf :hemorrhagic_disease ;
    :has_exact_match "MESH:D011693" ;
    :has_exact_synonym "Purpuric disorder"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:partial_motor_epilepsy rdfs:subClassOf :focal_epilepsy ;
    :has_exact_synonym "Focal motor seizure"@en,
        "epilepsy, focal motor"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:temporal_lobe_epilepsy rdfs:subClassOf :focal_epilepsy ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :focal_epilepsy ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :temporal_lobe ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A focal epilepsy that is characterized by recurrent, unprovoked focal seizures that originate in the temporal lobe of the brain and last about one or two minutes." ;
    :has_exact_synonym "epilepsy, temporal lobe"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:benign_epilepsy_with_centrotemporal_spikes rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :childhood_onset ],
        :childhood_electroclinical_syndrome ;
    :has_definition "A childhood electroclinical syndrome characterized by partial seizures involving the rolandic area of the brain and electroencephalographic centrotemporal sharp waves." ;
    :has_exact_synonym "BCECTS"@en,
        "benign Rolandic epilepsy"@en,
        "benign childhood epilepsy with centrotemporal spike"@en,
        "rolandic epilepsy"@en,
        "sylvan seizures"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:frontal_lobe_epilepsy rdfs:subClassOf :focal_epilepsy ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :focal_epilepsy ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :frontal_lobe ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A focal epilepsy that is characterized by recurring seizures that affect one hemisphere of the brain." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:haemonchiasis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :diarrhea ],
        :trichostrongyloidiasis ;
    :has_definition "A trichostrongyloidiasis that involves parasitic infection of the ruminant abomasum by nematodes of the genus Haemonchus resulting in anemia, dehydration, diarrhea and accumulation of fluid in the abdomen, gut wall, thoracic cavity and submandibular tissue." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:histrionic_personality_disorder rdfs:subClassOf :personality_disorder ;
    :has_definition "A personality disorder that is characterized by a pattern of excessive emotionality and attention-seeking, including an excessive need for approval and inappropriately seductive behavior, usually beginning in early adulthood." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:osteitis_fibrosa rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :hyperactivity ],
        :bone_resorption_disease ;
    :has_definition "A bone resorption disease that has_material_basis_in hyperparathyroidism which results_in hyperactivity in osteoclasts, deformity, and loss of mass located_in bone." ;
    :has_exact_synonym "Osteitis fibrosa cystica generalisata"@en,
        "Von Recklinghausen's bone disease"@en,
        "hyperparathyroid bone disease"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bone_inflammation_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :inflammation ],
        :bone_disease ;
    :has_definition "A bone disease that results_in inflammation of the located_in bone." ;
    :has_exact_synonym "Inflammatory disorder of bone"@en,
        "bone inflammatory disease"@en,
        "osteitis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:glycoproteinosis rdfs:subClassOf :mucolipidosis ;
    :has_definition "A mucolipidosis that is characterized by a deficiency of the enzyme alpha-N -acetyl neuraminidase (sialidase)." ;
    :has_exact_synonym "Mucolipidosis type I"@en,
        "sialidosis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:xanthomatosis rdfs:subClassOf :lipid_storage_disease ;
    :has_definition "A lipid storage disease that is characterized by the desposition of yellowish cholesterol-rich material in tendons or other body parts." ;
    :has_exact_synonym "xanthelasmatosis"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:osteosarcoma rdfs:subClassOf :bone_sarcoma ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :cancer ;
                    rdf:rest [ rdf:first [ owl:unionOf [ rdf:first [ a owl:Restriction ;
                                                    owl:onProperty :derives_from ;
                                                    owl:someValuesFrom :fibroblast ] ;
                                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                                            owl:onProperty :derives_from ;
                                                            owl:someValuesFrom :chondroblast ] ;
                                                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                                                    owl:onProperty :derives_from ;
                                                                    owl:someValuesFrom :osteoblast ] ;
                                                            rdf:rest :nil ] ] ] ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A bone sarcoma that is located_in bone that has_material_basis_in cells of mesenchymal origin. It usually involves bones and less frequently extraosseous sites. It often involves the long bones (particularly distal femur, proximal tibia, and proximal humerus)." ;
    :has_exact_synonym "Osteogenic sarcoma"@en,
        "Skeletal sarcoma"@en,
        "bone tissue neoplasm"@en,
        "osteoid sarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mesenchymal_cell_neoplasm rdfs:subClassOf :cell_type_cancer ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :cancer ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :derives_from ;
                                    owl:someValuesFrom :mesenchymal_cell ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A cell type cancer that has_material_basis_in abnormally proliferating cells derives from embryonic connective tissue that is capable of developing into connective tissue, such as bone, and cartilage, the lymphatic system, and the circulatory system." ;
    :has_exact_synonym "benign miscellaneous mesenchymal tumor"@en,
        "mesenchymal tumor"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bone_angioendothelial_sarcoma rdfs:subClassOf :bone_sarcoma ;
    :has_exact_synonym "osseous hemangiosarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:malignant_fibrous_histiocytoma_of_bone rdfs:subClassOf [ owl:unionOf [ rdf:first [ a owl:Restriction ;
                            owl:onProperty :derives_from ;
                            owl:someValuesFrom :fibroblast ] ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :derives_from ;
                                    owl:someValuesFrom :chondroblast ] ;
                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                            owl:onProperty :derives_from ;
                                            owl:someValuesFrom :osteoblast ] ;
                                    rdf:rest :nil ] ] ] ],
        :connective_tissue_cancer ;
    :has_exact_synonym "malignant fibrous Histiocytoma of the bone"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:fibrosarcoma_of_bone rdfs:subClassOf :bone_sarcoma ;
    :has_definition "A bone sarcoma characterized by a herringbone or fascicular disposition of atypical, monomorphic fibroblasts and is always negative for any specific marker." ;
    :has_exact_synonym "malignant fibroxanthoma",
        "malignant histiocytosis" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:fibrosarcoma rdfs:subClassOf :connective_tissue_cancer ;
    :has_definition "A connective tissue cancer that has_material_basis_in fibrous connective tissue and characterized by the presence of immature proliferating fibroblasts or undifferentiated anaplastic spindle cells in a storiform pattern." ;
    :has_exact_synonym "Fibrocytic tumor"@en,
        "fibrosarcoma of soft tissue"@en,
        "fibrous tissue neoplasm"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:localized_osteosarcoma rdfs:subClassOf [ owl:unionOf [ rdf:first [ a owl:Restriction ;
                            owl:onProperty :derives_from ;
                            owl:someValuesFrom :fibroblast ] ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :derives_from ;
                                    owl:someValuesFrom :chondroblast ] ;
                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                            owl:onProperty :derives_from ;
                                            owl:someValuesFrom :osteoblast ] ;
                                    rdf:rest :nil ] ] ] ],
        [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :bone_element ],
        :osteosarcoma ;
    :has_definition "An osteosarcoma that is confined to a specific site without evidence of spread to other anatomic sites." ;
    :has_exact_synonym "localised Osteogenic sarcoma"@en,
        "localised osteosarcoma"@en,
        "localized Osteogenic sarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:extraosseous_osteosarcoma rdfs:subClassOf [ owl:unionOf [ rdf:first [ a owl:Restriction ;
                            owl:onProperty :derives_from ;
                            owl:someValuesFrom :fibroblast ] ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :derives_from ;
                                    owl:someValuesFrom :chondroblast ] ;
                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                            owl:onProperty :derives_from ;
                                            owl:someValuesFrom :osteoblast ] ;
                                    rdf:rest :nil ] ] ] ],
        :osteosarcoma ;
    :has_definition "An osteosarcoma arising from the soft tissue." ;
    :has_exact_synonym "extraskeletal Osteogenic sarcoma"@en,
        "extraskeletal osteosarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:childhood_osteosarcoma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :childhood_onset ],
        :osteosarcoma ;
    :has_exact_synonym "pediatric osteosarcoma" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:coronary_aneurysm rdfs:subClassOf :heart_aneurysm ;
    :has_exact_synonym "Aneurysm of coronary vessels"@en,
        "Aneurysmal lesion of coronary artery"@en,
        "Arteriovenous aneurysm of coronary vessels"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ewing_sarcoma_of_bone rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :bone_element ],
        :bone_cancer,
        :ewing_sarcoma ;
    :has_definition "A peripheral primitive neuroectodermal tumor that is located_in bone." ;
    :has_exact_synonym "Ewing's sarcoma/bone peripheral primitive neuroectodermal tumor"@en,
        "bone Ewing sarcoma"@en,
        "bone localized Ewing sarcoma"@en,
        "bone localized Ewing's sarcoma"@en,
        "localized Skeletal Ewing's sarcoma"@en ;
    :has_related_synonym "Ewing's sarcoma of bone"@en,
        "bone Ewing's sarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ewing_sarcoma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :derives_from ;
            owl:someValuesFrom :embryonic_cell_metazoa_ ],
        :connective_tissue_cancer,
        :sarcoma ;
    :has_definition "A connective tissue cancer that has_material_basis_in neural crest cells derives_from undeveloped, undifferentiated neuroectoderm." ;
    :has_exact_synonym "Ewing's family localized tumor"@en,
        "Ewing's sarcoma/peripheral primitive neuroectodermal tumor"@en,
        "Ewing's tumor"@en,
        "Ewings sarcoma"@en,
        "Ewings sarcoma-primitive neuroectodermal tumor"@en,
        "PNET of Thoracopulmonary Region"@en,
        "localized Ewing sarcoma"@en,
        "localized Ewing's sarcoma"@en,
        "localized Ewing's sarcoma/peripheral primitive neuroectodermal tumor"@en,
        "localized Ewing's tumor"@en,
        "localized peripheral primitive neuroectodermal tumor"@en,
        "peripheral primitive neuroectodermal tumor"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:spinal_accessory_nerve_neoplasm rdfs:subClassOf :cranial_nerve_malignant_neoplasm ;
    :has_exact_synonym "XIth Cranial nerve tumors"@en,
        "neoplasm of accessory nerve"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chondrosarcoma rdfs:subClassOf :bone_sarcoma ;
    :has_definition "A bone sarcoma that has_material_basis_in cells derived from transformed cells that produce cartilage." ;
    :has_exact_synonym "Cartilaginous cancer"@en,
        "chondrosarcoma of bone"@en,
        "primary chondrosarcoma of the bone"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chondroblastic_osteosarcoma rdfs:subClassOf :osteosarcoma ;
    :has_exact_synonym "chondrosarcomatous Osteogenic sarcoma"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parosteal_osteosarcoma rdfs:subClassOf :peripheral_osteosarcoma ;
    :has_exact_synonym "Parosteal Osteogenic sarcoma"@en,
        "juxtacortical osteosarcoma"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:peripheral_osteosarcoma rdfs:subClassOf :bone_osteosarcoma ;
    :has_exact_synonym "Surface Osteosarcoma"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bone_osteosarcoma rdfs:subClassOf :osteosarcoma ;
    :has_definition "An osteosarcoma that is an usually aggressive malignant bone-forming mesenchymal neoplasm arising from the bone. It may arise de novo or from a pre-existing lesion of the bone. Pain and a palpable mass are the most frequent clinical sign and symptom. It may spread to other anatomic sites, particularly the lungs." ;
    :has_exact_synonym "Osteosarcoma of bone"@en,
        "primary Osteosarcoma of bone"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:small_cell_osteogenic_sarcoma rdfs:subClassOf :conventional_osteosarcoma ;
    :has_exact_synonym "Round cell osteosarcoma"@en,
        "small cell Osteosarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:liposarcoma_of_bone rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :bone_element ],
        [ owl:unionOf [ rdf:first [ a owl:Restriction ;
                            owl:onProperty :derives_from ;
                            owl:someValuesFrom :fibroblast ] ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :derives_from ;
                                    owl:someValuesFrom :chondroblast ] ;
                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                            owl:onProperty :derives_from ;
                                            owl:someValuesFrom :osteoblast ] ;
                                    rdf:rest :nil ] ] ] ],
        :bone_cancer ;
    :has_exact_synonym "Liposarcoma of the bone"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:liposarcoma rdfs:subClassOf :connective_tissue_cancer ;
    :has_definition "A connective tissue cancer that arises in fat cells in deep soft tissue retroperitoneum and the extremities." ;
    :has_exact_synonym "lipomatous cancer" .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bacterial_vaginosis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :bacteria ],
        :bacterial_infectious_disease,
        :vaginitis ;
    :has_definition "A vaginitis that is characterized by a grayish vaginal discharge usually of foul odor and the presence of Gardnerella vaginalis." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:periodontal_disease rdfs:subClassOf :mouth_disease ;
    :has_definition "A mouth disease that is relating to or affecting the structures surrounding and supporting the teeth." ;
    :has_exact_synonym "disease of supporting structures of teeth"@en,
        "periodontium disorder"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:papillon_lefevre_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :calcareous_tooth ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :ectodermal_dysplasia,
        :tooth_disease ;
    :has_definition "An ectodermal dysplasia that is characterized by palmoplantar keratoderma associated with early-onset periodontitis and has_material_basis_in homozygous or compound heterozygous mutation in the cathepsin C gene on chromosome 11q14." ;
    :has_exact_synonym "Papillon Lefevre syndrome"@en,
        "Papillon-Lefvre syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:accessory_nerve_disease rdfs:subClassOf :glossopharyngeal_nerve_disease ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :disease ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :accessory_xi_nerve ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A glossopharyngeal nerve disease that is characterized by involvement of the accessory nerve (eleventh cranial nerve)." ;
    :has_exact_synonym "Eleventh nerve disorder"@en,
        "disorder of 11th nerve"@en,
        "disorder of accessory [11th] nerve"@en,
        "disorder of accessory nerve"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:palmoplantar_keratosis rdfs:subClassOf :keratosis ;
    :has_definition "A keratosis characterized by abnormal thickening of the palms and the soles." ;
    :has_exact_synonym "palmo-plantar keratodermas",
        "Palmoplantar Keratoderma"@en,
        "Keratosis palmaris et plantaris"@fr .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:coronary_artery_disease rdfs:subClassOf :artery_disease ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :artery_disease ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :coronary_artery ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "An artery disease that is characterized by plaque building up along the inner walls of the arteries of the heart resulting in a narrowing of the arteries and a reduced blood supply to the cardiac muscles." ;
    :has_exact_synonym "CHD"@en,
        "Coronary disease"@en,
        "coronary arteriosclerosis"@en,
        "coronary heart disease"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:inappropriate_adh_syndrome rdfs:subClassOf :pituitary_gland_disease ;
    :has_definition "A pituitary gland disease resulting from excessive production of antidiuretic hormone (ADH)." ;
    :has_exact_match "MESH:D007177" ;
    :has_exact_synonym "SIADH",
        "syndrome of Inappropriate Antidiuretic Hormone Secretion"@en,
        "syndrome of inappropriate secretion of ADH"@en,
        "syndrome of inappropriate secretion of antidiuretic hormone"@en,
        "syndrome of inappropriate vasopressin secretion"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:histiocytosis rdfs:subClassOf :lymphatic_system_disease ;
    :has_definition "A lymphatic system disease that is characterized by an excessive number of histiocytes." ;
    :has_exact_synonym "Hand Schuller Christian disease"@en,
        "chronic Histiocytosis X"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:carotid_artery_disease rdfs:subClassOf :artery_disease,
        :cerebrovascular_disease ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :vascular_disease ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :carotid_artery_segment ] ;
                            rdf:rest :nil ] ] ] ;
    :has_exact_synonym "disorder of carotid artery"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:peripheral_vascular_disease rdfs:subClassOf :vascular_disease ;
    :has_definition "A vascular disease that is characterized by obstruction of larger arteries not within the coronary, aortic arch vasculature, or brain." ;
    :has_exact_synonym "arterial occlusive disease"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:carotid_artery_thrombosis rdfs:subClassOf :carotid_artery_disease,
        :intracranial_thrombosis ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :thrombosis ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :carotid_artery_segment ] ;
                            rdf:rest :nil ] ] ] .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:alpha_mannosidosis rdfs:subClassOf :lysosomal_storage_disease ;
    :has_definition "A lysosomal storage disease that has_material_basis_in deficiency of the alpha-D-manosidase enzyme resulting in the impairment of cell function from a build up of complex sugars derived from glycoproteins in the lysosome." ;
