@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cocaine_dependence rdfs:subClassOf :drug_dependence ;
    :has_definition "A drug dependence that is a psychological dependency on the regular use of cocaine." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:heroin_dependence rdfs:subClassOf :opiate_dependence ;
    :has_definition "An opiate dependence that involves the continued use of heroin despite problems related to use of the substance." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hallucinogen_dependence rdfs:subClassOf :drug_dependence ;
    :has_definition "A drug dependence that involves the continued use of hallucinogenic drugs despite problems related to use of the substance." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:eosinophilia_myalgia_syndrome rdfs:subClassOf :hypereosinophilic_syndrome ;
    :has_definition "A hypereosinophilic syndrome that is characterized by subacute onset of myalgias and peripheral eosinophilia, followed by chronic neuropathy and skin induration." ;
    :has_exact_synonym "Eosinophilia myalgia syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orbit_lymphoma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :lymphocyte ],
        :lymphoma,
        :orbital_cancer ;
    :has_definition "An orbital cancer that has_material_basis_in some lymphocyte." ;
    :has_exact_synonym "Lymphoma of the orbit"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orbit_sarcoma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :derives_from ;
            owl:someValuesFrom :embryonic_cell_metazoa_ ],
        :orbital_cancer,
        :sarcoma ;
    :has_definition "An orbital cancer that has_material_basis_in abnormally proliferating cells derived from embryonic mesoderm." ;
    :has_exact_synonym "orbital sarcoma"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:tertiary_neurosyphilis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :transmitted_by ;
            owl:someValuesFrom :contact_transmission ],
        :brain_disease,
        :spinal_cord_disease,
        :tertiary_syphilis ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :tertiary_syphilis ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :disease_has_location ;
                                    owl:someValuesFrom :brain ] ;
                            rdf:rest [ rdf:first [ a owl:Restriction ;
                                            owl:onProperty :disease_has_location ;
                                            owl:someValuesFrom :spinal_cord ] ;
                                    rdf:rest :nil ] ] ] ] ;
    :has_definition "A tertiary syphilis that results in infection located in brain or located in spinal cord." ;
    :has_exact_synonym "late neurosyphilis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hypereosinophilic_syndrome rdfs:subClassOf :leukocyte_disease ;
    :has_definition "A leukocyte disease that is characterized by high numbers of eosinophils which over time enter various tissues, eventually damaging organs." ;
    :has_exact_synonym "Eosinophilic leukocytosis"@en,
        "eosinophilia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hypoglycemia rdfs:subClassOf :glucose_metabolism_disease ;
    owl:equivalentClass [ owl:intersectionOf [ rdf:first :disease ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :has_phenotype ;
                                    owl:someValuesFrom :abnormal_blood_glucose_concentration ] ;
                            rdf:rest :nil ] ] ] ;
    :has_definition "A glucose metabolism disease that is characterized by abnormally low levels of blood glucose." ;
    :has_exact_synonym "Hypoglycaemia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:peripartum_cardiomyopathy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :muscle_weakness ],
        :dilated_cardiomyopathy ;
    :has_definition "A dilated cardiomyopathy that is characterized by a weakness of the heart muscle that begins sometime during the final month of pregnancy through about five months after delivery." ;
    :has_exact_synonym "antepartum peripartum cardiomyopathy"@en,
        "postpartum peripartum cardiomyopathy"@en .
