fragment:
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:omodysplasia rdfs:subClassOf :osteochondrodysplasia ;
    :has_definition "An osteochondrodysplasia that is characterized by severe limb shortening and facial dysmorphism." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ohdo_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :ptosis ],
        :syndrome ;
    :has_definition "A syndrome that is characterized by blepharophimosis, ptosis, dental hypoplasia, hearing impairment and intellectual disability." ;
    :has_exact_synonym "Ohdo blepharophimosis syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:blepharophimosis_intellectual_disability_syndrome_sbbys_type rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :ptosis ],
        :ohdo_syndrome ;
    :has_definition "A Ohdo syndrome that is characterized by blepharophimosis, ptosis and intellectual disability." ;
    :has_exact_synonym "Ohdo syndrome, SBBYS Variant"@en,
        "Say-Barber-Biesecker-Young-Simpson syndrome"@en ;
    :has_related_synonym "SBBYSS"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:oculodentodigital_dysplasia rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by craniofacial, neurologic, limb and ocular abnormalities." ;
    :has_exact_synonym "ODD syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:x_linked_chondrodysplasia_punctata_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        :chondrodysplasia_punctata,
        :x_linked_recessive_disease ;
    :has_definition "A chondrodysplasia punctata that is characterized by maxillary hypoplasia, stippled chondrodystrophy, flat nasal tip and short columella, has_material_basis_in X-linked recessive inheritance, has_material_basis_in deficiency of arylsulfatase E. and is associated with associated with vitamin K-related teratogenicity." ;
    :has_exact_synonym "chondrodystrophia calcificans congenita"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_dominant_chondrodysplasia_punctata rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :chondrodysplasia_punctata ;
    :has_definition "A chondrodysplasia punctata that is characterized by abnormal facies and stippling of the limbs, associated with vitamin K-related teratogenicity, has_material_basis_in autosomal dominant inheritance." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cold_induced_sweating_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome that is characterized by profuse sweating induced by cold ambient temperature." ;
    :has_exact_synonym "Crisponi syndrome"@en,
        "Sohar-Crisponi syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_2_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, has_material_basis_in autosomal recessive inheritance of mutation in the C2 gene." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:congenital_secretory_chloride_diarrhea_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :abnormality_of_prenatal_development_or_birth ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :diarrhea ],
        :autosomal_recessive_disease,
        :physical_disorder,
        :secretory_diarrhea ;
    :has_definition "A secretory diarrhea that has_material_basis_in mutation in the SLC26A3 gene." ;
    :has_exact_synonym "congenital chloride diarrhea finnish type"@en,
        "congenital chloride diarrhoea finnish type"@en,
        "congenital chloridorrhea"@en,
        "congenital secretory chloride diarrhoea 1"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_4a_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, caused by C4A deficiency." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_4b_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, caused by C4B deficiency." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_6_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, has_material_basis_in mutation in the C6 gene." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_7_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, has_material_basis_in mutation in the C7 gene." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:type_i_complement_component_8_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by deficiency of the alpha subunit of complement protein 8, which causes increased susceptibility to recurrent bacterial infections, especially to Neisseria meningitidis, and has_material_basis_in autosomal recessive inheritance of mutation in the C8A gene, which produces the alpha subunit of complement component 8 important in forming membrane attack complexes." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:type_ii_complement_component_8_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by deficiency of the beta subunit of complement protein 8, which causes increased susceptibility to recurrent bacterial infections, especially to Neisseria meningitidis, and has_material_basis_in autosomal recessive inheritance of mutation in the C8B gene, which produces the beta subunit of complement component 8 important in forming membrane attack complexes." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:complement_component_9_deficiency rdfs:subClassOf :complement_deficiency ;
    :has_definition "A complement deficiency that is characterized by recurrent bacterial infections, has_material_basis_in mutation in the C9 gene." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:dyschromatosis_universalis_hereditaria rdfs:subClassOf :pigmentation_disease ;
    :has_definition "A pigmentation disease characterized by reticulate hyper- and hypo-pigmentated macules in a generalized distribution." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:megalocornea rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        :corneal_disease,
        :x_linked_recessive_disease ;
    :has_definition "A corneal disease that is characterized by a bilaterally enlarged corneal diameter without an increase in intraocular pressure and that has_material_basis_in mutation in the CHRDL1 gene." ;
    :has_exact_synonym "anterior megalophthalmos"@en,
        "congenital anterior megalophthalmia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:meier_gorlin_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome that is characterized by bilateral underdevelopment of the external ear, short stature, absent or underdeveloped patellae and severe prenatal and postnatal growth retardation." ;
    :has_exact_synonym "ear-patella-short stature syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_dominant_intellectual_developmental_disorder rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :intellectual_disability ;
    :has_definition "A intellectual disability characterized by an autosomal dominant inheritance pattern." ;
    :has_exact_synonym "autosomal dominant mental retardation"@en,
        "autosomal dominant non-syndromic mental retardation"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_recessive_intellectual_developmental_disorder rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :intellectual_disability ;
    :has_definition "A intellectual disability characterized by an autosomal recessive inheritance pattern." ;
    :has_exact_synonym "autosomal recessive mental retardation"@en,
        "autosomal recessive non-syndromic mental retardation"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:syndromic_x_linked_intellectual_disability rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_inheritance ],
        :syndromic_intellectual_disability,
        :x_linked_monogenic_disease ;
    :has_definition "A syndromic intellectual disability characterized by an X-linked inheritance pattern." ;
    :has_exact_synonym "syndromic X-linked mental retardation"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:uvulitis rdfs:subClassOf :upper_respiratory_tract_disease ;
    :has_definition "An upper respiratory tract disease characterized by the swelling of the uvula to 3-5 times its normal size." ;
    :has_exact_synonym "acute uvulitis"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:adenoid_hypertrophy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_feature ;
            owl:someValuesFrom :adenoid_hypertrophy ],
        :upper_respiratory_tract_disease ;
    :has_definition "An upper respiratory tract disease characterized by the unusual growth of the adenoid tonsil; has symptom snoring, has symptom hyponasality, has symptom otitis media with effusion, has symptom mouth breathing." ;
    :has_exact_synonym "adenoidal hypertrophy"@en,
        "enlarged adenoids"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:angular_cheilitis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :inflammation ],
        :cheilitis ;
    :has_definition "A cheilitis characterized by inflammation of one or both of the corners of the mouth." ;
    :has_exact_synonym "angular cheilosis"@en,
        "angular stomatitis"@en,
        "cheilosis"@en,
        "commissural cheilitis"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:tracheomalacia rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :flaccidity ],
        :tracheal_disease ;
    :has_definition "A tracheal disease characterized by flaccidity of the tracheal support cartilage." ;
    :has_exact_synonym "congenital tracheomalacia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:persistent_generalized_lymphadenopathy rdfs:subClassOf :lymph_node_disease ;
    :has_definition "A lymph node disease characterized by enlarged, non-painful lymph nodes occurring for more than three to six months for which no other reason can be found." ;
    :has_exact_synonym "PGL"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:oral_hairy_leukoplakia rdfs:subClassOf :mouth_disease ;
    :has_definition "A mouth disease characterized by a white patch on the side of the tongue with a corrugated or hairy appearance; caused by Epstein-Barr virus." ;
    :has_exact_synonym "hairy leukoplakia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orofaciodigital_syndrome_i rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_dominant_inheritance ],
        :orofaciodigital_syndrome,
        :x_linked_dominant_disease ;
    :has_definition "An orofaciodigital syndrome that is characterized by malformations of the face, oral cavity, and digits, has_material_basis_in X-linked dominant inheritance of the OFD1 gene with lethality in males and is associated with polycystic kidney disease." ;
    :has_exact_synonym "Papillon-Leage-Psaume syndrome"@en,
        "orofaciodigital syndrome 1"@en,
        "orofaciodigital syndrome type I"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:lung_abscess rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :necrosis ],
        :lung_disease ;
    :has_definition "A lung disease characterized by microbial infection which causes a type of liquefactive necrosis of the pulmonary tissue and formation of cavities containing necrotic debris or fluid." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acute_promyelocytic_leukemia rdfs:subClassOf :acute_myeloid_leukemia ;
    :has_definition "An acute myeloid leukemia characterized by accumulation of promyelocytes in the bone marrow and by a translocation between chromosomes 15 and 17." ;
    :has_exact_synonym "acute myeloblastic leukaemia type 3"@en,
        "acute myeloblastic leukemia type 3"@en,
        "acute myeloid leukaemia M3"@en,
        "acute myeloid leukemia M3"@en,
        "acute promyelocytic leukaemia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cardiac_arrest rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :heart_failure ],
        :congestive_heart_failure ;
    :has_definition "A congestive heart failure characterized by a sudden stop in effective blood circulation due to the failure of the heart to contract effectively or at all." ;
    :has_exact_synonym "cardiopulmonary arrest"@en,
        "circulatory arrest"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:inguinal_hernia rdfs:subClassOf :intestinal_disease ;
    :has_definition "An intestinal disease characterized by a protrusion of abdominal cavity contests through the inguinal canal." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:umbilical_hernia rdfs:subClassOf :intestinal_disease ;
    :has_definition "A intestinal disease characterized by the protrusion by part of the intestine though an opening in the abdominal muscles." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mastoiditis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :middle_ear ],
        :bone_inflammation_disease,
        :middle_ear_disease ;
    :has_definition "A middle ear disease characterized by an inflammation of the mucosal lining of the mastoid antrum and the mastoid air cell system inside the mastoid process." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:breast_abscess rdfs:subClassOf :breast_disease ;
    :has_definition "A breast disease characterized by a collection of pus in the breast." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:dental_abscess rdfs:subClassOf :tooth_disease ;
    :has_definition "A tooth disease characterized by a localized collection of pus associated with a tooth." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cervical_polyp rdfs:subClassOf :cervix_disease ;
    :has_definition "A cervix disease characterized by a benign polyp on the surface of the cervical canal." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:myelomeningocele rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_location ;
            owl:someValuesFrom :spinal_cord ],
        :spina_bifida,
        :spinal_cord_disease ;
    :has_definition "A spina bifida characterized by protrusion of the spinal cord through an opening, covered by meningeal membranes." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:omphalocele rdfs:subClassOf :physical_disorder ;
    :has_definition "A physical disorder characterized by a defect in the development of the abdominal wall muscles, resulting in the intestines, liver and other organs to remain outside of the abdomen in a sac." ;
    :has_exact_synonym "omphalocoele"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:anal_fistula rdfs:subClassOf :rectal_disease ;
    :has_definition "An anus disease characterized by is an abnormal connection between the epithelialised surface of the anal canal and the perianal skin." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ectopic_pregnancy rdfs:subClassOf :female_reproductive_system_disease ;
    :has_definition "A female reproductive system disease characterized by the implantation of the embryo outside the uterine cavity." ;
    :has_exact_synonym "eccyesis"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:rapp_hodgkin_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :ectodermal_dysplasia ;
    :has_definition "An ectodermal dysplasia characterized by abnormal development of ectodermal tissues including the skin, hair, nails, teeth and sweat glands and anhidrotic ectodermal dysplasia with cleft lip/palate." ;
    :has_exact_synonym "RHS"@en,
        "anhidrotic ectodermal dysplasia with cleft lip/palate"@en,
        "ectodermal dysplasia syndrome, Rapp-Hodgkin type"@en,
        "ectodermal dysplasia, Rapp-Hodgkin type"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mitochondrial_complex_v_atp_synthase_deficiency_nuclear_type_2 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :mitochondrial_complex_v_atp_synthase_deficiency ;
    :has_definition "A mitochondrial complex V (ATP synthase) deficiency that has_material_basis_in mutation in the TMEM70 gene on chromosome 8q21." ;
    :has_exact_synonym "MC5DN2"@en,
        "neonatal mitochondrial encephalocardiomyopathy due to ATP synthase deficiency"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mitochondrial_complex_v_atp_synthase_deficiency_nuclear_type_3 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :mitochondrial_complex_v_atp_synthase_deficiency ;
    :has_definition "A mitochondrial complex V (ATP synthase) deficiency that has_material_basis_in mutation in the ATP5E gene on chromosome 20q13." ;
    :has_exact_synonym "MC5DN3"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mitochondrial_complex_v_atp_synthase_deficiency_nuclear_type_4 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :mitochondrial_complex_v_atp_synthase_deficiency ;
    :has_definition "A mitochondrial complex V (ATP synthase) deficiency that has_material_basis_in mutation in the ATP5F1A gene on chromosome 18q21.1." ;
    :has_exact_synonym "MC5DN4"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:transient_neonatal_diabetes_mellitus rdfs:subClassOf :neonatal_diabetes ;
    :has_definition "A neonatal diabetes that is characterized by hyperglycemia during the neonatal period that remits during infancy but recurs in later life in most patients." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_dominant_sideroblastic_anemia_4 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :sideroblastic_anemia ;
    :has_definition "A sideroblastic anemia characterized by an autosomal dominant inheritance pattern." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:3_methylglutaconic_aciduria rdfs:subClassOf :organic_acidemia ;
    :has_definition "An organic acidemia that is characterized by elevated levels of 3-methylglutaconic acid and 3-methylglutaric acid in the urine." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:cednik_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome that has_material_basis_in homozygous mutation in the SNAP29 gene and characterized by a unique constellation of clinical manifestations including microcephaly, severe neurologic impairment, psychomotor retardation, failure to thrive, facial dysmoprhism, palmoplantar keratoderma and late-onset ichthyosis." ;
    :has_exact_synonym "cerebral dysgenesis, neuropathy, ichthyosis and keratoderma syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parameningeal_embryonal_rhabdomyosarcoma rdfs:subClassOf :embryonal_rhabdomyosarcoma ;
    :has_definition "An embryonal rhabdomyosarcoma located in the parameningeal region." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chronic_atrial_and_intestinal_dysrhythmia rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by a unique combination of cardiac arrhythmias and intestinal pseudo-obstruction. It has_material_basis_in the mutated SGOL1 protein. Distinctive clinical features include atrial dysrhythmias, sick sinus syndrome (SSS) and valve anomalies and chronic intestinal pseudo-obstruction (CIPO)." ;
    :has_exact_synonym "CAID syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:ciliopathy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :gene ],
        :monogenic_disease,
        :syndrome ;
    :has_definition "A syndrome associated with mutations encoding defective proteins, which result in either abnormal function formation or function of cilia." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:agnathia_otocephaly_complex rdfs:subClassOf :physical_disorder ;
    :has_definition "A physical disorder characterized by mandibular hypoplasia or agnathia, ventromedial auricular malposition (melotia) and/or auricular fusion (synotia), and microstomia with oroglossal hypoplasia or aglossia. Holoprosencephaly is the most commonly identified association, but skeletal, genitourinary and cardiovascular anomalies and situs inversus have been reported." ;
    :has_exact_synonym "agnathia-holoprosencephaly-situs inversus syndrome"@en,
        "dysgnathia complex agnathia-holoprosencephaly"@en,
        "holoprosencephaly-agnathia"@en,
        "otocephaly"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acromelic_frontonasal_dysostosis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :dysostosis ;
    :has_definition "A dysostosis characterized by cranium bifidum, severe hypertelorism, median cleft lip and palate, nasal bifurcation, brachycephaly, large fontanelle, tibial hemimelia, preaxial polydactyly of the feet and brain malformations." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:glucocorticoid_induced_osteoporosis rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :ptosis ],
        :osteoporosis ;
    :has_definition "An osteoporosis caused by chronic glucocorticoid use. Glucocorticoids impair the replication, differentiation and function of osteoblasts and induce the apoptosis of mature osteoblasts and osteocytes; the also favor osteoclastogenesis leading to an increase in bone resorption." ;
    :has_exact_synonym "steroid-induced osteoporosis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acrodermatitis_chronica_atrophicans rdfs:subClassOf :acrodermatitis ;
    :has_definition "An acrodermatitis characterized by a chronically progressive course, leading to widespread atrophy of the skin. It is a clinical manifestation of Lyme borreliosis." ;
    :has_exact_synonym "Herxheimer disease"@en,
        "primary diffuse atrophy"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:bacillary_angiomatosis rdfs:subClassOf :bartonellosis ;
    :has_definition "A bartonellosis that has_material_basis_in Bartonella henselae or has_material_basis_in Bartonella quintana. The disease is characterized by the proliferation of blood vessels, resulting in them forming tumour-like masses in the skin and other organs." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:native_american_myopathy rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :ptosis ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :muscle_weakness ],
        :autosomal_recessive_disease,
        :congenital_myopathy ;
    :has_definition "A congenital myopathy that is characterized by congenital weakness, arthrogryposis, cleft palate, ptosis, myopathic facies, short stature, kykphoscoliosis, talipes deformities and susceptibility to malignant hyperthermia provoked by anesthesia and that has_material_basis_in homozygous mutation in the STAC3 gene on chromosome 12q13." ;
    :has_exact_synonym "Bailey-Bloch congenital myopathy",
        "congenital myopathy 13" .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acrorenal_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by limb defects, usually bilateral, like cleft hands or feet and longitudinal defects involving radius or ulna, tibia or fibula and renal anomalies which include agenesis, hypoplasia and rarely polycystic kidneys. Additional malformations may involve the oro-mandibular region, the trachea and lungs, skin derivatives including sweat glands, mammary glands, the uterus, vas deferens, the nasal placodes and the eyes." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hypoparathyroidism_retardation_dysmorphism_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by permanent parathyroid hormone (PTH) deficiency, hypocalcemia, hyperphosphatemia, facial anomalies, and psychomotor retardation that has_material_basis_in homozygous or compound heterozygous mutation in the TBCE gene on chromosome 1q42.3." ;
    :has_exact_synonym "HRD syndrome"@en,
        "Sanjad-Sakati syndrome"@en,
        "hypoparathyroidism with short stature, mental retardation and seizures"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:microcephaly_with_or_without_chorioretinopathy_lymphedema_or_mental_retardation rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by variable expression of microcephaly, ocular disorders including chorioretinopathy, congenital lymphedema of the lower limbs, and mild to moderate intellectual disability." ;
    :has_exact_synonym "chorioretinal dysplasia-microcephaly-mental retardation syndrome"@en,
        "lymphedema and retinal folds with ficrocephaly and microphthalmos"@en,
        "lymphedema, microcephaly and chorioretinopathy syndrome"@en,
        "microcephaly lymphedema chorioretinal dysplasia"@en,
        "microcephaly, lymphedema, chorioretinal dysplasia syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:adenine_phosphoribosyltransferase_deficiency rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :purine_pyrimidine_metabolic_disorder ;
    :has_definition "A purine-pyrimidine metaobolic disorder characterized by the formation 2,8-dihydroxyadenine stones and renal failure secondary to intratubular crystalline precipitation and has_material_basis_in homozygous or compound heterozygous mutation in the gene encoding adenine phosphoribosyltransferase (APRT) on chromosome 16q24." ;
    :has_exact_synonym "2,8-dihydroxyadenine urolithiasis"@en,
        "APRT deficiency"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mitochondrial_complex_iii_deficiency_nuclear_type_2 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :mitochondrial_complex_iii_deficiency ;
    :has_definition "A mitochondrial metabolism disease characterized by motor disability, with ataxia, apraxia, dystonia, and dysarthria, associated with necrotic lesions throughout the brain and has_material_basis_in mutation in the TTC19 gene on chromosome 17. It has an autosomal recessive inheritance pattern." ;
    :has_exact_synonym "MC3DN2"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:kleefstra_syndrome_1 rdfs:subClassOf [ owl:intersectionOf [ rdf:first [ a owl:Restriction ;
                            owl:onProperty :has_material_basis_in ;
                            owl:someValuesFrom :deletion ] ;
                    rdf:rest [ rdf:first [ a owl:Restriction ;
                                    owl:onProperty :has_material_basis_in ;
                                    owl:someValuesFrom :chromosome ] ;
                            rdf:rest :nil ] ] ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :chromosomal_deletion_syndrome,
        :kleefstra_syndrome ;
    :has_definition "A Kleefstra syndrome that is characterized by severe mental retardation, hypotonia, microcephaly, epileptic seizures, flat face with hypertelorism, synophrys, anteverted nares, everted lower lip, carp mouth with macroglossia, and heart defects and that has_material_basis_in a microdeletion in the chromosome region 9q34.3 or by a point mutation in the EHMT1 gene located in that region." ;
    :has_exact_synonym "9q subtelomeric deletion syndrome"@en,
        "9q-syndrome"@en,
        "9q34 deletion syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:acrofacial_dysostosis_cincinnati_type rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_phenotype ;
            owl:someValuesFrom :micrognathia ],
        [ a owl:Restriction ;
            owl:onProperty :has_phenotype ;
            owl:someValuesFrom :malar_flattening ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :acrofacial_dysostosis,
        :autosomal_dominant_disease ;
    :has_definition "An acrofacial dysostosis characterized by a spectrum of mandibulofacial dysostosis phenotypes, such as cleft palate, micrognathia, malar flattening, microcephaly and, in some cases, extrafacial skeletal defects. It is that has_material_basis_in heterozygous mutation in the POLR1A gene on chromosome 2p11." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:stormorken_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :blood_platelet_disease ;
    :has_definition "A blood platelet disease characterized by thrombocytopathy, thrombocytopenia, mild anemia, asplenia, tubular aggregate myopathy, miosis, headache, and ichthyosis. It has_material_basis_in heterozygous mutation in the STM1 gene on chromosome 11p15. It has an autosomal dominant inheritance pattern." ;
    :has_exact_synonym "thrombocytopathy, asplenia and miosis"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:amyotrophic_lateral_sclerosis_type_22 rdfs:subClassOf :amyotrophic_lateral_sclerosis ;
    :has_definition "An amyotrophic lateral sclerosis that has_material_basis_in mutation in the TUBA4A gene on chromosome 2q35." ;
    :has_exact_synonym "ALS 22"@en,
        "amyotrohpic lateral sclerosis 22 with or without frontotemporal dementia"@en,
        "amyotrophic lateral sclerosis 22"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:vici_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by callosal agenesis, cataracts, cardiomyopathy, combined immunodeficiency and hypopigmentation. It has_material_basis_in mutation in the EPG5 gene on chromosome 18q12.3." ;
    :has_exact_synonym "immunodeficiency with cleft lip/palate, cataract, hypopigmentation, and absent corpus callosum"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chylomicron_retention_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :lipid_metabolism_disorder ;
    :has_definition "A lipid metabolism disorder characterized by malnutrition, failure to thrive, growth failure, vitamin E deficiency and the absence of chylomicrons and apolipoprotein B48 post-prandially. It has an autosomal recessive inheritance pattern and has_material_basis_in mutations in the SAR1B gene on chromosome 5q31.1." ;
    :has_exact_synonym "Anderson disease"@en,
        "CMRD"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:multiple_acyl_coa_dehydrogenase_deficiency rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        [ a owl:Restriction ;
            owl:onProperty :has_phenotype ;
            owl:someValuesFrom :abnormal_metabolism ],
        :inherited_metabolic_disorder ;
    :has_definition "An inherited metabolic disorder characterized by the body's inability to break down proteins and fats to produce energy. It is a disorder of fatty acid, amino acid, and choline metabolism and has an autosomal recessive inheritance pattern. It has_material_basis_in mutations in the ETFA, ETFB and ETFDH genes. It presents three clinical phenotypes: a neonatal-onset form with congenital anomalies (type I), a neonatal-onset form without congenital anomalies (type II), and a late-onset form (type III). The neonatal-onset forms are usually fatal." ;
    :has_exact_synonym "MAD deficiency"@en,
        "MADD"@en,
        "electron transfer flavoprotein deficiency"@en,
        "electron transfer flavoprotein ubiquinone oxidoreductase deficiency"@en,
        "glutaric acidemia type 2"@en,
        "glutaric aciduria type 2"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:sakati_nyhan_syndrome rdfs:subClassOf :acrocephalosyndactylia ;
    :has_definition "An acrocephalosyndactylia characterized by abnormalities in the bones of the legs, congenital heart defects and craniofacial defects and craniosynostosis. The patients suffer from cyanosis and other respiratory and breathing infections." ;
    :has_exact_synonym "ACPS with leg hypoplasia"@en,
        "Sakati syndrome"@en,
        "Sakati-Nyhan-Tisdale syndrome"@en,
        "acrocephalopolysyndactyly Type III"@en,
        "acrocephalopolysyndactyly type 3"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hereditary_papulotranslucent_acrokeratoderma rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :papule ],
        :cholesteatoma ;
    :has_definition "A keratosis of the hands and feet characterized by persistent, asymptomatic, yellowish to white papules and plaques associated with fine-textured scalp hair and an atopic diathesis." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:punctate_palmoplantar_keratoderma rdfs:subClassOf :palmoplantar_keratosis ;
    :has_definition "A palmoplantar keratosis characterized by keratoses with a raindrop pattern on the palmoplantar surface, skin lesions which may involve the whole of the palmoplantar surface, or may be more restricted in their distribution." ;
    :has_exact_synonym "punctate palmoplantar hyperkeratosis"@en ;
    :has_related_synonym "punctate keratosis palmoplantaris"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:punctate_palmoplantar_keratoderma_type_iii rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :punctate_palmoplantar_keratoderma ;
    :has_definition "A punctate palmoplantar keratoderma that is characterized by hyperkeratinization of the palms and soles, has_material_basis_in autosomal dominant inheritance of mutation in the AAGAB gene." ;
    :has_exact_synonym "acrokeratoelastoidosis of Costa"@en,
        "punctate palmoplantar hyperkeratosis type 3"@en,
        "punctate palmoplantar keratoderma type 3"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:glycerol_kinase_deficiency rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :structural_variant ],
        :inherited_metabolic_disorder ;
    :has_definition "An inherited metabolic disorder characterized_by wide range of phenotypic variability; patients can have severe metabolic and CNS abnormalities, while others possess hyperglycerolemia and glyceroluria with no other apparent phenotype and that has_material_basis_in mutation in the GK gene on chromosome Xp21." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:galloway_mowat_syndrome_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :galloway_mowat_syndrome ;
    :has_definition "A Galloway-Mowat syndrome that has_material_basis_in homozygous mutation in the WDR73 gene on chromosome 15q25." ;
    :has_exact_synonym "SCAR5",
        "autosomal recessive spinocerebellar ataxia 5",
        "Galloway syndrome"@en,
        "microcephaly, hiatal hernia and nephrotic syndrome"@en,
        "nephrosis-microcephaly syndrome"@en,
        "nephrosis-neuronal dysmigration syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:mandibulofacial_dysostosis_with_alopecia rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by malar and mandibular hypoplasia, typically associated with abnormalities of the ears and eyelids, and with alopecia." ;
    :has_exact_synonym "MFDA"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hennekam_syndrome rdfs:subClassOf :lymphatic_system_disease ;
    :has_definition "A lymphatic system disease characterized by he presence of intestinal lymphangiectasia, mental retardation, and characteristic facial anomalies. It is inherited in an autosomal recessive pattern. Most individuals with Hennekam syndrome have characteristic facial abnormalities, such as a flat face with accompanying puffy eyelids and hypertelorism; the nasal bridge is typically flat and the ears are typically small. Congenital extremity and genital lymphedema is present in most patients." ;
    :has_exact_synonym "Hennekam lymphangiectasia-lymphedema syndrome"@en,
        "lymphedem-lymphangiectasia-intellectual disability syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :late_onset_parkinson_s_disease ;
    :has_definition "A late onset Parkinson's disease that has_material_basis_in mutation in the alpha-synuclein gene on chromosome 4q22.1." ;
    :has_exact_synonym "autosomal dominant Parkinson disease 1"@en,
        "autosomal dominant Parkinson's disease 1"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_2 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :early_onset_parkinson_s_disease ;
    :has_definition "An early-onset Parkinson's disease that has_material_basis_in mutation in the parkin gene on chromosome 6q25.2-q27." ;
    :has_exact_synonym "autosomal recessive juvenile Parkinson disease 2"@en,
        "autosomal recessive juvenile Parkinson's disease 2"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_6 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :digenic_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :digenic_disease,
        :early_onset_parkinson_s_disease ;
    :has_definition "An early-onset Parkinson's disease that has_material_basis_in mutations in the PINK1 gene on chromosome 1p36.12." ;
    :has_exact_synonym "PARK6",
        "autosomal recessive early-onset Parkinson disease 6"@en,
        "autosomal recessive early-onset Parkinson's disease 6"@en,
        "early-onset Parkinson disease 6"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_7 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :early_onset_parkinson_s_disease ;
    :has_definition "An early-onset Parkinson's disease that has_material_basis_in homozygous or compound heterozygous mutation in the DJ1 gene on chromosome 1p36." ;
    :has_exact_synonym "autosomal recessive early-onset Parkinson disease 7"@en,
        "autosomal recessive early-onset Parkinson's disease 7"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_8 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :late_onset_parkinson_s_disease ;
    :has_definition "A late onset Parkinson's disease that has_material_basis_in heterozygous mutation in the dardarin encoding gene on chromosome 12q12." ;
    :has_exact_synonym "autosomal dominant Parkinson disease 8"@en,
        "autosomal dominant Parkinson's disease 8"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:parkinson_s_disease_15 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :early_onset_parkinson_s_disease ;
    :has_definition "An early-onset Parkinson's disease that has_material_basis_in mutation in the FBXO7 gene on chromosome 22q12.3." ;
    :has_exact_synonym "Parkinsonian-pyramidal syndrome"@en,
        "autosomal recessive early-onset Parkinson disease 15"@en,
        "autosomal recessive early-onset Parkinson's disease 15"@en,
        "pallidopyramidal syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orofaciodigital_syndrome_iii rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :orofaciodigital_syndrome ;
    :has_definition "An orofaciodigital syndrome that is characterized by dysmorphic facies and severe intellectual deficits, and has_material_basis_in autosomal recessive inheritance." ;
    :has_exact_synonym "Sugarman syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orofaciodigital_syndrome_iv rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :orofaciodigital_syndrome ;
    :has_definition "An orofaciodigital syndrome that is characterized by dysmorphic facies, the development of harmartomas of the tongue, polydactyly and limb dysplasia, has_material_basis_in autosomal recessive inheritance of mutations in the TCTN3 gene." ;
    :has_exact_synonym "Baraitser-Burn syndrome"@en,
        "OFD4"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:orofaciodigital_syndrome_v rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :orofaciodigital_syndrome ;
    :has_definition "An orofaciodigital syndrome that is characterized by postaxial polydactyly and median cleft of the upper lip and has_material_basis_in homozygous mutation in the DDX59 gene on chromosome 1q32." ;
    :has_exact_synonym "OFD5"@en,
        "orofaciodigital syndrome Thurston type"@en,
        "polydactyly, postaxial, with median cleft of upper lip"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:joubert_syndrome_with_orofaciodigital_defect rdfs:subClassOf :joubert_syndrome ;
    :has_definition "A Joubert syndrome that is characterized by orofaciodigital defect." ;
    :has_exact_synonym "OFD6"@en,
        "Polydactyly cleft lip palate psychomotor retardation"@en,
        "Varadi syndrome"@en,
