    :has_exact_synonym "deletion 13q14"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chromosome_14q11_q22_deletion_syndrome rdfs:subClassOf :chromosomal_deletion_syndrome ;
    :has_definition "A chromosomal deletion syndrome that is characterized by microcephaly, dysmorphic facies, psychomotor delay and failure to thrive, has_material_basis_in isolated cases of partial deletion of the long arm of chromosome 14." ;
    :has_exact_synonym "14q11.2 microdeletion syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:chromosome_15q11_2_deletion_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :chromosomal_deletion_syndrome ;
    :has_definition "A chromosomal deletion syndrome that is characterized by intellectual disbaility, dysmorphic facies, psychiatric illness and autism spectrum disorder, has_material_basis_in autosomal dominant inheritance of partial deletion of the long arm of chromosome 15." ;
    :has_exact_synonym "15q11.2 microdeletion syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

