    :has_exact_synonym "15q26 deletion syndrome"@en,
        "Drayer syndrome"@en,
        "distal 15q deletion syndrome"@en,
        "distal monosomy 15q"@en,
        "telomeric 15q deletion syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

