        "UMLS_CUI:C3150701",
        "UMLS_CUI:C4518824" ;
    :has_exact_synonym "distal 16p11.2 microdeletion syndrome" .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

