    :has_exact_synonym "17q23.1-q23.2 microdeletion syndrome"@en,
        "17q23.1q23.2 microdeletion syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

