            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :chromosomal_deletion_syndrome ;
    :has_definition "A chromosomal deletion syndrome that has_material_basis_in a chromosome 19q13.11 deletion and that is characterized by characterized by poor overall growth, slender habitus, microcephaly, delayed development, intellectual disability with poor or absent speech, and feeding difficulties." ;
    :has_exact_synonym "19q13.11 microdeletion syndrome"@en,
        "monosomy 19q13.11"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
