            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by variable combinations of microcephaly, limb malformations, esophageal and duodenal atresias, and learning disability/mental retardation." ;
    :has_exact_synonym "FGLDS"@en,
        "MODED syndrome"@en,
        "ODED syndrome"@en,
        "digital anomalies with short palpebral fissures and atresia of esophagus or duodenum"@en,
        "microcephaly-digital anomalies-normal intelligence syndrome"@en,
        "microcephaly-oculo-digito-esophageal-duodenal syndrome"@en,
        "oculo-digito-esophageal-duodenal syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:fibrochondrogenesis rdfs:subClassOf :osteochondrodysplasia ;
    :has_definition "An osteochondrodysplasia that is characterized by shortened long bones in the arms and legs that are unusually wide at the ends, flattened vertebrae with a characteristic pinched or pear shape, and a very narrow chest in infants with short, wide ribs and a round and prominent abdomen." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:gingival_fibromatosis rdfs:subClassOf :gingival_overgrowth ;
    :has_definition "A gingival overgrowth characterized by benign, slowly progressive, nonhemorrhagic, fibrous enlargement of the oral masticatory mucosa." ;
    :has_exact_synonym "hereditary gingival fibromatosis"@en,
        "hereditary gingival hyperplasia"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:humeroradial_synostosis rdfs:subClassOf :synostosis ;
    :has_definition "A synostosis that is characterized by uni- or bilateral fusion of the humerus and radius bones at the elbow level, with or without associated ulnar and carpal/metacarpal deficiency, leading to loss of elbow motion and, in many cases, functional arm incapacity." ;
    :has_related_synonym "humero-radial fusion"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

