        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by severe intellectual disability, epilepsy, scoliosis, choreoathetosis, dysmorphic facial features and altered dermal pigmentation that has_material_basis_in homozygous or compound heterozygous mutation in the SIAT9 gene on chromosome 2p11.2." ;
    :has_exact_synonym "salt & pepper syndrome"@en,
        "salt-and-pepper syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:fetal_valproate_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by distinctive facial appearance, a cluster of minor and major anomalies and central nervous system dysfunction." ;
    :has_exact_synonym "fetal valproic acid syndrome"@en,
        "foetal valproate syndrome"@en,
        "foetal valproic acid syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

