    :has_definition "A skin disease characterized by congenital blistering, skin atrophy, photosensitivity, skin fragility, and scaling." ;
    :has_exact_synonym "hereditary acrokeratotic poikiloderma of Kindler-Weary"@en,
        "poikiloderma of Kindler"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:kabuki_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by multiple congenital anomalies and mental retardation. Other characteristics include a peculiar facial gestalt, short stature, skeletal and visceral abnormalities, cardiac anomalies, and immunological defects." ;
    :has_exact_synonym "KMS"@en,
        "Kabuki make up syndrome"@en,
        "Niikawa-Kuroki syndrome"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

