            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        [ a owl:Restriction ;
            owl:onProperty :existence_starts_during ;
            owl:someValuesFrom :childhood_onset ],
        :autosomal_dominant_disease,
        :idiopathic_generalized_epilepsy ;
    :has_definition "An idiopathic generalized epilepsy characterized by onset of multiple seizure types in the first few years of life and associated with poor prognosis. Affected individuals have cognitive regression and intellectual disability and that has_material_basis_in heterozygous mutation in the SLC6A1 gene on chromosome 3p25." ;
    :has_exact_synonym "childhood onset epileptic encephalopathy",
        "EEOC"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:perlman_syndrome rdfs:subClassOf :syndrome ;
    :has_definition "A syndrome characterized by polyhydramnios with neonatal macrosomia, nephromegaly, distinctive facial appearance, renal dysplasia, nephroblastomatosis, and predisposition to Wilms tumor. It shows similarities to Beckwith-Wiedemann syndrome." ;
    :has_exact_synonym "nephroblastomatosis - fetal ascites - macrosomia - Wilms tumor"@en,
        "nephroblastomatosis, fetal ascites, macrosomia and Wilms tumor"@en,
        "renal hamartomas, nephroblastomatosis and fetal gigantism"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:zika_fever rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :fever ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :rash ],
        [ a owl:Restriction ;
            owl:onProperty :transmitted_by ;
            owl:someValuesFrom :aedes_genus_ ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :headache ],
        [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :joint_pain ],
        :viral_infectious_disease ;
    :has_definition "A viral infectious disease that has_material_basis_in Zika virus, which is transmitted_by Aedes aegypti mosquitoes and targets neural progenitor cells and neuronal cells in all stages of maturity and has_symptom fever, has_symptom rash, has_symptom headaches and has_symptom joint pain." ;
    :has_exact_synonym "Zika virus disease"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

