    :has_definition "An isolated microphthalmia characterized by autosomal recessive inheritance reduction in eye size that is restricted to the posterior segment of the eye, extreme hyperopia, and short axial length that has_material_basis_in homozygous or compound heterozygous mutation in the PRSS56 gene on chromosome 2q37." ;
    :has_exact_synonym "MCOP6"@en,
        "posterior nonsyndromic microphthalmia"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_4 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :disease_has_basis_in ;
            owl:someValuesFrom :gene ],
        :isolated_microphthalmia,
        :monogenic_disease ;
    :has_definition "An isolated microphthalmia that has_material_basis_in mutation in the GDF6 gene on chromosome 8q22." ;
    :has_exact_synonym "MCOP4"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_5 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia characterized by autosomal recessive inheritance of posterior microphthalmia, hypermetropia, night blindness, decreased visual acuity, reduced macular reflex, scleral thickening, impared rod and cone responses on ERG, foveoschisis and in some patients retinal pigment epithelium atrophy, arteriolar attenuation, angle-closure glaucoma and optic disc drusen that has_material_basis_in homozygous or compound heterozygous mutation in the MFRP gene on chromosome 11q23." ;
    :has_exact_synonym "MCOP5"@en,
        "microphthalmia-retinitis pigmentosa-foveoschisis-optic disc drusen syndrome"@en,
        "posterior microphthalmia with retinitis pigmentosa, foveoschisis and optic disc drusen"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_7 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia characterized by unilateral microphthalmia that has_material_basis_in caused by mutation in the GDF3 gene on chromosome 12p13." ;
    :has_exact_synonym "MCOP7"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_2 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia characterized by autosomal recessive inheritance of bilateral profound microphthalmia without associated anomalies and with normal intelligence that has_material_basis_in homozygous mutation in the CHX10 gene on chromosome 14q24." ;
    :has_exact_synonym "MCOP2"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia that has_material_basis_in variation in the chromosomal region 14q32." ;
    :has_exact_synonym "MCOP1"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_8 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia characterized by bilateral severe microphthalmia autosomal recessive inheritance of that has_material_basis_in homozygous mutation in the ALDH1A3 gene on chromosome 15q26." ;
    :has_exact_synonym "MCOP8"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:isolated_microphthalmia_3 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :isolated_microphthalmia ;
    :has_definition "An isolated microphthalmia characterized by clinical anophthalmia and/or microphthalmia that has_material_basis_in compound heterozygous mutation in the RAX gene on chromosome 18q21." ;
    :has_exact_synonym "MCOP3"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:hereditary_neuropathy_with_liability_to_pressure_palsies rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_symptom ;
            owl:someValuesFrom :muscle_weakness ],
        [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :neuropathy ;
    :has_definition "A neuropathy characterized by autosomal dominant inheritance of peroneal muscle weakness, peripheral neuropathy, hyporeflexia, tomacula, segmental demyelination/remyelination, decreased motor nerve conduction that has_material_basis_in deletion or point mutation of the PMP22 gene on chromosome 17p12." ;
    :has_exact_synonym "HNPP"@en,
        "current pressure-sensitive neuropathy"@en,
        "familial recurrent polyneuropathy"@en,
        "heterozygous microdeletion 17p11.2p12"@en,
        "potato-grubbing palsy"@en,
        "tomaculous neuropathy"@en,
        "tulip-bulb digger's palsy"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:norrie_disease rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_recessive_inheritance ],
        :syndrome,
        :x_linked_recessive_disease ;
    :has_definition "A syndrome characterized by degenerative and proliferative changes of the neuroretina resulting in congenital blindness along with progressive mental disorders in about 50% of patients and sensorineural deafness in about 33% of patients, and that has_material_basis_in mutation in the NDP gene on chromosome Xp11." ;
    :has_exact_synonym "Episkopi blindness"@en,
        "Norrie-Warburg disease"@en,
        "atrophia bulborum hereditaria"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:leri_weill_dyschondrosteosis rdfs:subClassOf :osteochondrodysplasia ;
    :has_definition "An osteochondrodysplasia characterized by abnormal shortening of the forearms and lower legs, abnormal misalignment of the wrist (Madelung deformity of the wrist), and associated short stature and has_material_basis_in heterozygous defects in the pseudoautosomal genes SHOX or SHOXY or by deletion of the SHOX downstream regulatory domain." .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:developmental_and_epileptic_encephalopathy_9 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :x_linked_dominant_inheritance ],
        :developmental_and_epileptic_encephalopathy,
        :x_linked_dominant_disease ;
    :has_definition "A developmental and epileptic encephalopathy characterized by seizure onset in infancy and mild to severe intellectual impairment in females that has_material_basis_in heterozygous mutation in the gene encoding protocadherin-19 (PCDH19) on chromosome Xq22." ;
    :has_exact_synonym "DEE9",
        "early infantile epileptic encephalopathy 9",
        "EFMR"@en,
        "EIEE9"@en,
        "Juberg Hellman syndrome"@en,
        "early infantile female-limited epilecptic encephalopathy"@en,
        "female restricted epilepsy with mental retardation"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:osteoporosis_pseudoglioma_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by congenital or infancy-onset blindness, very low bone mass, decreased trabecular bone volume, severe juvenile-onset osteoporosis and spontaneous fractures, pseudoglioma, microphthalmia that has_material_basis_in homozygous or compound heterozygous mutation in the LRP5 gene on chromosome 11q13." ;
    :has_exact_synonym "OPPG"@en,
        "ocular form of osteogenesis imperfecta"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:annular_pancreas rdfs:subClassOf :pancreas_disease ;
    :has_definition "A pancreas disease characterized by autosomal dominant inheritance of the annular pancreas (head of the pancreas forming a ring around the second portion of the duodenum) and duodenal stenosis." .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:pemphigus_vulgaris rdfs:subClassOf :pemphigus ;
    :has_definition "A pemphigus characterized by autosomal dominant blisters and erosions on the skin and mucous membranes erosions cause by autoantibodies to intercellular cement substance." ;
    :has_exact_synonym "familial pemphigus vulgaris"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:pierson_syndrome rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by nephrotic syndrome with diffuse mesangial sclerosis, proteinuria, microcoria, absence of the pupillary dilator muscle in the iris, ciliary muscle atrophy, and abnormal eye development with lens-shape, retinal and corneal anomalies that has_material_basis_in homozygous or compound heterozygous mutation in the LAMB2 gene on chromosome 3p21." ;
    :has_exact_synonym "microcoria-congenital nephrosis syndrome"@en .

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:potocki_lupski_syndrome rdfs:subClassOf :chromosomal_duplication_syndrome ;
    :has_definition "A chromosomal duplication syndrome characterized by hypotonia, failure to thrive, mental retardation, pervasive developmental disorders and congenital anomalies that has_material_basis_in duplication of a region of chromosome 17p11.2." ;
    :has_exact_synonym "17p11.2 microduplication syndrome"@en,
        "chromosome 17p11.2 duplication syndrome"@en,
        "trisomy 17p11.2"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_recessive_pseudohypoaldosteronism_type_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :pseudohypoaldosteronism ;
    :has_definition "A pseudohypoaldosteronism characterized by enal salt wasting and high concentrations of sodium in sweat, stool, and saliva that has_material_basis_in homozygous or compound heterozygous mutation in any one of 3 genes encoding subunits of the epithelial sodium channel (ENaC): SCNN1A, SCNN1B, or SCNN1G." ;
    :has_exact_synonym "PHA1B"@en,
        "autosomal recessive PHA 1"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:autosomal_dominant_pseudohypoaldosteronism_type_1 rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_dominant_inheritance ],
        :autosomal_dominant_disease,
        :pseudohypoaldosteronism ;
    :has_definition "A pseudohypoaldosteronism characterized by Salt wasting resulting from renal unresponsiveness to mineralocorticoids that has_material_basis_in heterozygous mutation in the NR3C2 gene on chromosome 4q31." ;
    :has_exact_synonym "PHA1A"@en,
        "autosomal dominant PHA 1"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:right_atrial_isomerism rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_recessive_inheritance ],
        :autosomal_recessive_disease,
        :visceral_heterotaxy ;
    :has_definition "A visceral heterotaxy that is characterized by complete atrioventricular septal defect with a common atrium and univentricular AV connection, total anomalous pulmonary drainage, and transposition or malposition of the great arteries and that has_material_basis_in homozygous mutation in the GDF1 gene on chromosome 19p12." ;
    :has_exact_synonym "Ivemark syndrome"@en,
        "asplenia with cardiovascular anomalies"@en .

@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

:septooptic_dysplasia rdfs:subClassOf [ a owl:Restriction ;
            owl:onProperty :has_material_basis_in ;
            owl:someValuesFrom :autosomal_inheritance ],
        :autosomal_genetic_disease,
        :syndrome ;
    :has_definition "A syndrome characterized by the classical triad of optic nerve hypoplasia, pituitary gland hypoplasia and midline brain defects that has_material_basis_in mutation in the HESX1 gene on chromosome 3p14." ;
    :has_exact_synonym "De Morsier syndrome"@en,
        "SOD"@en,
        "septo-optic dysplasia"@en .
